mapper.insert:

    SELECT IFNULL(MAX(BOARD_NO), 1) FROM T_BOARD

INSERT INTO T_BOARD (
    BOARD_TITLE,
    BOARD_CONTENT,
    BOARD_WRITER
) VALUES (
    #{boardTitle},
    #{boardContent},
    #{boardWriter}
)

mapper.update:
UPDATE T_BOARD_FILE
    SET
        BOARD_FILE_NAME = #{boardFileName},
        BOARD_FILE_PATH = #{boardFilePath},
        BOARD_FILE_ORIGIN = #{boardFileOrigin},
        BOARD_FILE_CATE = #{boardFileCate}
    WHERE BOARD_NO = #{boardNo}
      AND BOARD_FILE_NO = #{boardFileNo}

mapper.delete:
DELETE FROM T_BOARD_FILE
    WHERE BOARD_NO = #{boardNo}
      AND BOARD_FILE_NO = #{boardFileNo}
